import React, { Component } from "react";
import "../Styles/App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      add_product_button_status: false,
      formInput: {
        query: "",
        topic: "",
        currentTag: "",
        tagList: [],
      },
    };
  }

  handleAddProductButtonStatus = () => {
    this.setState({
      add_product_button_status: !this.state.add_product_button_status,
    });
  };

  handleChange = (e) => {
    this.setState({
      formInput: {
        ...this.state.formInput,
        currentTag: e.target.value,
      },
    });
  };

  handleAddTagButton = (e) => {
    e.preventDefault();
    const { tagList, currentTag } = this.state.formInput;
    tagList.push(currentTag);
    this.setState({
      formInput: { ...this.state.formInput, tagList: tagList },
      currentTag: "",
    });
  };

  render() {
    const { add_product_button_status } = this.state;
    const { tagList } = this.state.formInput;
    return (
      <div className="App">
        <div className="container">
          <div className="search-input">
            <input placeholder="Search" />
            <ul className="result">
              <li>Abc</li>
              <li>Def</li>
              <li>GHI</li>
            </ul>
          </div>
          <div className="add-question-container">
            <button onClick={this.handleAddProductButtonStatus}>
              {add_product_button_status ? (
                <i className="fa fa-times" aria-hidden="true"></i>
              ) : (
                "Add Question"
              )}
            </button>
            {add_product_button_status && (
              <form>
                <div className="input-container">
                  <div className="lable">Query :</div>
                  <input placeholder="Query" />
                </div>
                <div className="input-container">
                  <div className="lable">Topic :</div>
                  <input placeholder="Topic" />
                </div>
                <div className="input-container">
                  <div className="lable">Tags :</div>
                  <input
                    placeholder="Tags in comma separated form"
                    onChange={this.handleChange}
                  />
                  <ul>
                    {tagList.map((item) => {
                      return <li>item</li>;
                    })}
                  </ul>
                  <button onClick={this.handleAddTagButton}>Add Tag</button>
                </div>

                <button>Add</button>
              </form>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
