import React, { Component } from "react";

class AddQuestion extends Component {
  constructor(props) {
    super(props);
    this.state = {
      add_product_button_status: false,
      formInput: {
        query: "",
        topic: "",
        currentTag: "",
        tagList: [],
      },
    };
  }

  handleAddProductButtonStatus = () => {
    this.setState({
      add_product_button_status: !this.state.add_product_button_status,
    });
  };

  handleChange = (label, value) => {
    if (label === "query") {
      this.setState({
        formInput: {
          ...this.state.formInput,
          query: value,
        },
      });
    } else if (label === "topic") {
      this.setState({
        formInput: {
          ...this.state.formInput,
          topic: value,
        },
      });
    } else if (label === "currentTag") {
      this.setState({
        formInput: {
          ...this.state.formInput,
          currentTag: value,
        },
      });
    }
  };

  handleAddTagButton = (e) => {
    e.preventDefault();
    const { tagList, currentTag } = this.state.formInput;
    tagList.push(currentTag);
    this.setState({
      formInput: { ...this.state.formInput, tagList: tagList, currentTag: "" },
    });
  };

  render() {
    const { add_product_button_status } = this.state;
    const { tagList, currentTag } = this.state.formInput;
    return (
      <div className="add-question-container">
        <button onClick={this.handleAddProductButtonStatus}>
          {add_product_button_status ? (
            <i className="fa fa-times" aria-hidden="true"></i>
          ) : (
            "Add Question"
          )}
        </button>
        {add_product_button_status && (
          <form>
            <div className="input-container">
              <div className="lable">Query :</div>
              <input placeholder="Query" />
            </div>
            <div className="input-container">
              <div className="lable">Topic :</div>
              <input placeholder="Topic" />
            </div>
            <div className="input-container">
              <div className="lable">Tags :</div>
              <input
                placeholder="Tags in comma separated form"
                onChange={this.handleChange}
                value={currentTag}
              />
              <ul>
                {tagList.map((item) => {
                  return <li>{item}</li>;
                })}
              </ul>
              <button
                onClick={(e) => {
                  this.handleAddTagButton("currentTag", e.target.value);
                }}
              >
                Add Tag
              </button>
            </div>

            <button>Add</button>
          </form>
        )}
      </div>
    );
  }
}

export default AddQuestion;
