import React, { Component } from "react";
import "../Styles/App.scss";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      add_product_button_status: true,
    };
  }

  handleAddProductButtonStatus = () => {
    this.setState({
      add_product_button_status: !this.state.add_product_button_status,
    });
  };

  render() {
    return (
      <div className="App">
        <div className="container">
          <div className="search-input">
            <input placeholder="Search" />
            <ul className="result">
              <li>Abc</li>
              <li>Def</li>
              <li>GHI</li>
            </ul>
          </div>
          <div className="add-question-container">
            <button onClick={this.handleAddProductButtonStatus}>
              {this.state.add_product_button_status ? (
                "Add Question"
              ) : (
                <i className="fa fa-times" aria-hidden="true"></i>
              )}
            </button>
            <form>
              <div className="input-container">
                <div className="lable">Query :</div>
                <input placeholder="Query" />
              </div>
              <div className="input-container">
                <div classNames="lable">Topic :</div>
                <input placeholder="Topic" />
              </div>
              <div className="input-container">
                <div className="lable">Tags :</div>
                <input placeholder="Tags in comma separated form" />
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
