import React, { Component } from "react";
import { APIUrls } from "../helpers/urls";
import { getFormBody } from "../helpers/utils";
class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      result: [],
      isLoading: false,
    };
    this.intervalId = null;
  }

  fetchResult = (searchKey) => {
    console.log("a");
    this.setState({ isLoading: true });
    const url = APIUrls.getSearchResult();
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: getFormBody({ searchKey: searchKey }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(this.state, "state", data.data.questions);
        this.setState({ result: data.data.questions });
        setTimeout(() => {
          this.setState({ isLoading: false });
        }, 500);
      });
  };

  handleChange = (e) => {
    console.log(e.target.value);
    let context = this;
    if (e.target.value.length > 0) {
      clearInterval(this.intervalId);
      this.intervalId = setTimeout((context, fetchResult) => {
        context.fetchResult();
      }, 500);
      this.fetchResult(e.target.value);
    }
  };
  render() {
    const { result, isLoading } = this.state;
    return (
      <div className="search-input">
        <input placeholder="Search" onChange={this.handleChange} />
        <ul className="result">
          {isLoading ? (
            <li>Loading</li>
          ) : result.length === 0 ? (
            <li>No Result Found</li>
          ) : (
            result.map((r) => {
              return <li>{r.query}</li>;
            })
          )}
        </ul>
      </div>
    );
  }
}

export default Search;
